String dataStream = ""

int startOfPacketMarker = 4
int startOfMessageMarker = 14

static int getIndexOfStarter(String dataStream, int markerLength){
    int counter = 0
    List<String> packets = new ArrayList<String>()
    boolean shouldContinue = true
    dataStream.split('').eachWithIndex { String character, Integer index ->
        if(packets.size() < markerLength) {
            packets.add(character)
        }else {
            List<String> tmpPacketList = new ArrayList<String>()
            tmpPacketList.addAll(packets)
            if(tmpPacketList.unique().size() == markerLength && shouldContinue) {
                counter = index
                shouldContinue = false
            }else {
                packets.pop()
                packets.add(character)
            }
        }
    }
    return counter
}

println(getIndexOfStarter(dataStream, startOfPacketMarker))
println(getIndexOfStarter(dataStream, startOfMessageMarker))